// Filtruj zadania po terminie i statusie
const allTasks = $input.all();
const now = new Date();
const currentHour = now.getHours();
const currentMinute = now.getMinutes();
const currentDate = now.toISOString().split('T')[0]; // Format: yyyy-mm-dd

const overdueTasks = [];

for (const task of allTasks) {
  // Sprawd≈∫ status - pomi≈Ñ zako≈Ñczone zadania
  const status = (task.json.Status || task.json.status || '').toLowerCase().trim();
  if (status === 'completed' || status === 'wykonane' || status === 'zako≈Ñczone') {
    continue;
  }

  // Sprawd≈∫ datƒô - tylko zadania na dzisiaj
  const taskDate = task.json.data || task.json.date || '';
  if (taskDate !== currentDate) {
    continue;
  }

  // Sprawd≈∫ godzinƒô wykonania
  const scheduledTime = task.json['godzina wykonania'] || task.json.scheduled_time || '';
  if (!scheduledTime || !scheduledTime.match(/^\d{2}:\d{2}$/)) {
    continue; // Pomi≈Ñ zadania bez godziny
  }

  // Parsuj godzinƒô wykonania
  const [scheduledHour, scheduledMinute] = scheduledTime.split(':').map(Number);

  // Sprawd≈∫ czy zadanie jest po terminie
  const isOverdue = 
    scheduledHour < currentHour || 
    (scheduledHour === currentHour && scheduledMinute <= currentMinute);

  if (isOverdue) {
    overdueTasks.push({
      json: {
        ...task.json,
        scheduled_time: scheduledTime,
        overdue_minutes: (currentHour - scheduledHour) * 60 + (currentMinute - scheduledMinute)
      }
    });
  }
}

return overdueTasks;

